pragma solidity ^0.8.20;

/**
 * WARNING: This contract is intentionally vulnerable to reentrancy.
 * Use ONLY for learning, local CTFs, or training environments.
 */
contract ReentrancyBountyVault {
    mapping(address => uint256) public balances;
    bool public bountyClaimed;

    event Deposit(address indexed user, uint256 amount);
    event Withdraw(address indexed user, uint256 amount);
    event BountyClaimed(address indexed attacker, uint256 amount);

    // Anyone can deposit ETH
    function deposit() external payable {
        require(msg.value > 0, "no value");
        balances[msg.sender] += msg.value;
        emit Deposit(msg.sender, msg.value);
    }

    // Vulnerable withdraw – classic reentrancy bug
    function withdraw(uint256 amount) external {
        require(balances[msg.sender] >= amount, "insufficient balance");

        // ❌ External call BEFORE state update
        (bool ok, ) = msg.sender.call{value: amount}("");
        require(ok, "transfer failed");

        // ❌ State updated after external call → reentrancy window
        balances[msg.sender] -= amount;

        emit Withdraw(msg.sender, amount);
    }

    // Simple bounty: first to drain contract below a threshold gets a "bounty"
    function claimBounty() external {
        // e.g. if contract balance < 0.1 ETH, attacker can claim this bounty
        require(address(this).balance < 0.1 ether, "vault not drained enough");
        require(!bountyClaimed, "bounty already claimed");

        bountyClaimed = true;

        uint256 bountyAmount = address(this).balance;
        (bool ok, ) = msg.sender.call{value: bountyAmount}("");
        require(ok, "bounty payout failed");

        emit BountyClaimed(msg.sender, bountyAmount);
    }

    // Helper view
    function contractBalance() external view returns (uint256) {
        return address(this).balance;
    }
}

